    private void butonRegistroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_butonRegistroActionPerformed
        // TODO add your handling code here:
        String usuario = usuario_info.getText();
        String pw="";   
        try {
            pw = Utilidad.codificarPW(pw_info.getText());
        } catch (Exception ex) {
            Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
        }
        String nombre = nombre_info.getText();
        String apellido = ape_info.getText();
        String email = email_info.getText();
        String cedula = cedula_info.getText();
        
        if (Utilidad.isValidEmailAddress(email) == false){
            JOptionPane.showMessageDialog(null, "Email ingresado es invalido");
        }
        else{
            
            String tipoUsuario = (String)comboCuenta.getSelectedItem();
            if(tipoUsuario.equals("Customer")){
                String max_Ofertar = max_ofertar.getText();
                EntityManagerFactory emfCuenta = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
                CuentaJpaController controCuentaCliente = new CuentaJpaController(emfCuenta);
                EntityManagerFactory emfCliente = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
                ClienteJpaController controCliente = new ClienteJpaController(emfCliente);
                Cliente cli = new Cliente();
                Cuenta cu = new Cuenta();
                
                cu.setApellido(apellido);
                cu.setDeleted('N');
                cu.setEmail(email);
                cu.setContrasenia(pw);
                cu.setNombre(nombre);
                cu.setTipo(tipoUsuario);
                cu.setUsuario(usuario);
                try {
                    controCuentaCliente.create(cu);
                } catch (Exception ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                }
                cli.setCedula(BigInteger.valueOf(Integer.parseInt(cedula)));
                cli.setUsuario(usuario);
                cli.setMaxRentaOfertar(BigInteger.valueOf(Integer.parseInt(max_Ofertar)));
                cli.setCuenta(cu);
                try {
                    controCliente.create(cli);
                    JOptionPane.showMessageDialog(null, "Cuenta del cliente creada correctamente");
                } catch (PreexistingEntityException ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                } catch (Exception ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                }
            }
            else{
                EntityManagerFactory emfCuenta = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
                CuentaJpaController controCuentaOwner = new CuentaJpaController(emfCuenta);
                EntityManagerFactory emfOwner = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
                PropietarioJpaController controPropi = new PropietarioJpaController(emfOwner);
                Propietario pro = new Propietario();
                Cuenta cu = new Cuenta();
                
                cu.setApellido(apellido);
                cu.setDeleted('N');
                cu.setContrasenia(pw);
                cu.setNombre(nombre);
                cu.setEmail(email);
                cu.setTipo(tipoUsuario);
                cu.setUsuario(usuario);
                try {
                    controCuentaOwner.create(cu);
                } catch (Exception ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                }
                pro.setCedula(BigInteger.valueOf(Integer.parseInt(cedula)));
                pro.setCuenta(cu);
                pro.setUsuario(usuario);
                pro.setNumeroPropiedades(BigInteger.valueOf(0));
                try {
                    controPropi.create(pro);
                    JOptionPane.showMessageDialog(null, "Cuenta de propietario creada correctamente");
                } catch (Exception ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                }
                
                
            }
        }
        
            
        
    }//GEN-LAST:event_butonRegistroActionPerformed

