    private void jButton8ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton8ActionPerformed
        // TODO add your handling code here:
        int res = JOptionPane.showConfirmDialog(null, "Esta apunto de eliminar esta cuenta ¿Desea continuar?");
        if(res==0){
            String texto = user_info.getText();
            EntityManagerFactory emfCuenta = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
            CuentaJpaController controCuenta = new CuentaJpaController(emfCuenta);
            Cuenta c = controCuenta.findCuenta(texto);
            if(c.getTipo().equals("Customer")){
                c.setDeleted('Y');
                try {
                    controCuenta.edit(c);
                } catch (NonexistentEntityException ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                } catch (Exception ex) {
                    Logger.getLogger(GUIinmobiliaria_1.class.getName()).log(Level.SEVERE, null, ex);
                }
                JOptionPane.showMessageDialog(null, "Su cuenta fue borrada exitosamente");
                tabbedPane.setVisible(false);
                LogIn.setVisible(true);
                user_info.setText("");
                pwd_info.setText("");
            }else{
                c.setDeleted('Y');
                EntityManagerFactory emfOwner2 = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
                PropietarioJpaController controProp = new PropietarioJpaController(emfOwner2);
                EntityManagerFactory emfPropiedad = Persistence.createEntityManagerFactory("InmobiliariaFinalx3PU");
                PropiedadJpaController controPropiedad = new PropiedadJpaController(emfPropiedad);
                for(Propiedad p : controPropiedad.findPropiedadEntities()){
                    if(p.getPropiedadPK().getUsuario().equals(c.getUsuario())){
                        p.setDeleted('Y');  
                    }
                }
                JOptionPane.showMessageDialog(null, "Su cuenta fue borrada exitosamente");
                tabbedPane.setVisible(false);
                LogIn.setVisible(true);
                user_info.setText("");
                pwd_info.setText("");
            }
        }
        
    }//GEN-LAST:event_jButton8ActionPerformed
    
